INSERT INTO `rares` (`word`, `def`, `pronounc`, `lexinfo`, `examples`, `def2`, `def3`, `def4`, `def5`, `def6`) VALUES
('Abadir', 'Terme de Mythologie. C\'est le nom d\'une pierre que Saturne devora au lieu de Jupiter. (Furetière 1690).', '(a-ba-dir)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abalourdir', 'Rendre balourd, hébété. Populaire.', '(a-ba-lour-dir)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abaque', 'Terme d\'architecture. Tailloir, partie supérieure du chapiteau des colonnes, sur laquelle porte l\'architrave.', '(a-ba-k\')', 'n. m.', NULL, 'Terme d\'antiquité. Tableau couvert de poussière, sur lequel on traçait des nombres et on enseignait le calcul.', NULL, NULL, NULL, NULL),
('Abat-faim', 'Terme de cuisine. Pièce de résistance qu\'on sert la première sur table.', '(a-ba-fin)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abator', 'Qui est entré en possession. Ce mot, dans les Tenures de Littleton, désigne celui qui s\'est mis en possession, qui s\'est saisi d\'un héritage.', '(a-ba-tor)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abat-sons', 'Se dit des lames de bois recouvertes de plomb ou d\'ardoises qui garantissent les beffrois de la pluie et renvoient le son vers le sol.', '(a-ba-son)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abattis', 'Terme de fortification. Défense accessoire consistant en un amas d\'arbres entrelacés, liés ensemble et arrêtés sur le sol.', '(a-ba-tî)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abattures', 'Terme de chasse. Trace qu\'un cerf laisse dans les broussailles où il a passé.', '(a - ba - tur\')', 'n. f. plur.', 'Le cerf se reconnaît à ses abattures.', NULL, NULL, NULL, NULL, NULL),
('Abave', 'Bisaïeul. Du latin abavus, de même qu\'on a dit ave ou ayeul du latin avus, grand-père.', NULL, 'n. m.', '\" Abave, grand ave \" (Bouteill. Som. Rur. p. 464.),.', NULL, NULL, NULL, NULL, NULL),
('Abeausir', 'Marine. Se mettre au beau. Le temps s\'abeausit.', '(S\') (a-bô-sir)', 'v. pr.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abée', 'Ouverture par laquelle coule l\'eau qui fait aller un moulin. On l\'a aussi définie ouverture par où l\'eau a son cours quand les moulins ne tournent pas.', '(a-bée)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abeillage', 'Droit Seigneurial. Laurière le définit \" un droit en vertu duquel les abeilles épaves et non poursuivies, appartiennent aux Seigneurs Justiciers. \".', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aber', 'Profond estuaire de rivière en Bretagne.', '(a-bèr)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abergeage', 'Ancien terme de jurisprudence. Contrat primitif et première concession, que le seigneur faisait de son fonds à son premier emphytéote.', '(a-bèr-ja-j\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aberhavre', 'Embouchure de fleuve qui forme un port.', '(a-bèr-ha-vr\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ab hoc et ab hâc', 'Confusément, sans raison.', '(a-bo-ké-ta-bak)', 'loc. adv. et famil.', 'Il parle ab hoc et ab hâc.', NULL, NULL, NULL, NULL, NULL),
('Abienneurs', 'Séquestres.', NULL, 'n. m. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abigéat', 'Terme d\'ancien droit criminel. Délit de celui qui détourne les troupeaux d\'autrui pour se les approprier.', '(a-bi-jé-a)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abîme', 'Terme de blason. Centre de l\'écu lorsqu\'il porte une ou plusieurs pièces qui ne chargent aucune des autres.', '(a-bî-m\')', 'n. m.', 'Il porte trois besans d\'or, avec une fleur de lis en abîme.', NULL, NULL, NULL, NULL, NULL),
('Ab intestat', 'Terme de jurisprudence. A la suite d\'une mort sans testament.', '(a-bin-tes-ta)', 'loc. adv.', 'Héritier ab intestat, succession ab intestat. Dix têtes viennent ab intestat partager sa succession, LA BRUYÈRE.', NULL, NULL, NULL, NULL, NULL),
('Abiotique', 'Terme didactique. Où l\'on ne peut vivre.', '(a-bi-o-ti-k\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abir', 'Jugement, sens, esprit. ...', NULL, 'n. m.', 'Vous avar tant d\'abir, Bien saurés lors miaus coisir. Albir a eu la même signification dans le patois Provençal.', NULL, NULL, NULL, NULL, NULL),
('Ab irato', 'Sous l\'influence de la colère.', '(a-bi-ra-to)', 'loc. adv.', 'Lettre écrite ab irato. Testament fait ab irato.', NULL, NULL, NULL, NULL, NULL),
('Ablais', 'Blés coupés qui sont encore dans le champ.', '(a-blê)', 'n. m. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abluer', 'Laver, passer légèrement une liqueur préparée avec de la noix de galle sur du parchemin ou du papier, pour faire revivre l\'écriture.', '(a-blu-é)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aboillage', 'Certain droit que des Seigneurs Chastelains avaient de prendre les abeilles dans les forests dependantes de leurs Chastelenies. Ce mot vient de ce qu\'on disoit autrefois aboille, pour abeille. (Furetière 1690).', NULL, 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aborner', 'Mettre des bornes à un terrain.', '(a-bor-né)', 'v. t.', 'Faire aborner son champ.', NULL, NULL, NULL, NULL, NULL),
('Abot', 'Espèce d\'entrave que l\'on met au paturon pour retenir les chevaux.', '(a-bo)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aboucher', 'Mettre face à face, en conférence.', '(a-bou-ché)', 'v. t.', 'Je voulais en secret vous aboucher tous deux, MOLIÈRE. S\'ABOUCHER, v. pr. Conférer avec quelqu\'un. Ils se sont abouchés, et sont convenus de la marche à suivre.', NULL, NULL, NULL, NULL, NULL),
('Abradant', 'Qui racle, ratisse ou gratte.', NULL, 'adj.', 'On a dit au figuré : \" les Méridionaux sont paillards à cause de la mélancholie spumeuse, abradante, et salace. \" Charron.', NULL, NULL, NULL, NULL, NULL),
('Abraxas', 'Pierre précieuse sur laquelle étaient gravés des caractères et qu\'on portait en amulette.', '(a-bra-ksas\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abrouti', 'Terme d\'eaux et forêts.', '(a-brou-ti, tie)', 'adj.', 'Le bois est abrouti quand les premières pousses ont été mangées par le bétail et sont mal venues.', NULL, NULL, NULL, NULL, NULL),
('Abscons', 'Difficile à comprendre.', '(ab-skon)', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Absidiole', 'Terme d\'architecture. Petite abside.', '(ab-si-di-o-l\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Abstème', 'Qui ne boit pas de vin.', '(ab-stê-m\')', 'n. m. et f.', 'Telle qu\'est celle (l\'exception) des abstèmes, qui ne peuvent boire de vin, BOSSUET. Nous serions tous abstèmes si l\'on ne nous eût donné du vin dans nos jeunes ans, ROUSSEAU.', NULL, NULL, NULL, NULL, NULL),
('Abstrus', 'Difficilement accessible à l\'entendement.', '(ab-stru, struz\')', 'adj.', 'Une recherche si abstruse et si embarrassante, BOSSUET.', NULL, NULL, NULL, NULL, NULL),
('Abuter', 'Terme de marine. Mettre bout à bout, ou toucher par un bout. Ces pièces de bois abutent.', '(a-bu-té)', 'v. t. et v. int.', NULL, 'Au jeu de boule ou de palet, lancer la boule ou le palet vers un but pour savoir qui jouera le premier. Eh bien, abutons.', NULL, NULL, NULL, NULL),
('Acade', 'Sillage. Oudin, dans son Dict.', NULL, 'n. m.', 'Explique le mot acade, ou erre d\'un vaisseau, par le sillage.', NULL, NULL, NULL, NULL, NULL),
('Acagnarder', 'Rendre cagnard. La mauvaise compagnie l\'a acagnardé, Acad.', '(a-ka-gnar-dé)', 'v. t.', NULL, 'V. pr. Devenir cagnard. S\'acagnarder dans un fauteuil. Ces enfants se sont acagnardés au coin du feu. XVIe s. Je ne me peux contenter de moi mesme, me voyant ici oisif, acagnardé à un foyer, YVER.', NULL, NULL, NULL, NULL),
('Acanthe', 'Plante dite vulgairement branche-ursine et remarquable par ses belles feuilles découpées et recourbées vers l\'extrémité.', '(a-kan-t\')', 'n. f.', 'On a dit que la feuille d\'acanthe avait servi de modèle pour l\'ornement du chapiteau corinthien. Voici la fête d\'Olympie ! Tressez l\'acanthe et le laurier, V. HUGO.', 'Ornement d\'architecture imité de la feuille d\'acanthe.', NULL, NULL, NULL, NULL),
('Acare', 'Parasite de la classe des arachnides responsable de la gale humaine.', '(a-ka-r\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acatalectique', 'Terme de métrique ancienne. On appelait ainsi les vers auxquels il ne manquait aucune syllabe.', '(a-ka-ta-lé-kti-k\')', 'n. m. et adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acaule', 'Terme de botanique. Qui n\'a pas de tige apparente.', '(a-kô-l\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Accointer', 'Faire accointance. Il s\'est accointé d\'un homme, avec un homme de fort mauvaise réputation.', '(S\') (a-koin-té)', 'v. pr.', NULL, NULL, NULL, NULL, NULL, NULL),
('Accoiser', 'Rendre coi, calme, tranquille. Adoucissons, lénifions et accoisons l\'aigreur de ses esprits, MOLIÈRE.', '(a-koi-zé)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Accon', 'Bateau à fond plat qui cale fort peu d\'eau, et qui sert principalement, dans les Antilles, au chargement des navires de commerce. Bateau à fond plat employé dans la pèche des huîtres. Petit bateau à fond plat, qui sert à aller sur des vases, quand la mer est retirée.', '(a-kon)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Accordailles', 'Réunion pour signer un contrat de mariage.', '(a-kor-dâ-l\' ; ll mouillées, et non a-kor-dâ-ye)', 'n. f. plur.', 'Il se trouva peu de parents aux accordailles.', NULL, NULL, NULL, NULL, NULL),
('Accore', 'Terme de marine. Con tour d\'un banc, d\'un écueil.', '(a-ko-r\')', 'n. m.', NULL, 'Pièce de bois qu\'on dresse pour étayer. Les accores sont des étançons ou fortes pièces de bois qui servent à étayer un vaisseau en construction ou en réparation.', 'Adj. Une côte, une terre est accore, quand elle est coupée verticalement à la surface de la mer ou fortement inclinée.', NULL, NULL, NULL),
('Accouer', 'Attacher des chevaux ensemble, de manière que le licou de celui qui suit soit lié à la queue de celui qui précède ; de la sorte ces animaux marchent à la file.', '(a-kou-é)', 'v. t.', NULL, 'Se dit de l\'action du veneur qui suit le cerf et le joint pour lui donner le coup au défaut de l\'épaule ou lui couper le jarret.', NULL, NULL, NULL, NULL),
('Accroire', 'Usité seulement à l\'infinitif et avec faire, faire croire ce qui n\'est pas vrai.', '(a-kroi-r\')', 'v. t.', 'Faire accroire.', 'En faire accroire, conter des sornettes à quelqu\'un, le tromper par de belles paroles.', 'S\'en faire accroire, présumer trop de soi-même, s\'attribuer un mérite qu\'on n\'a pas.', NULL, NULL, NULL),
('Accrue', 'Agrandissement d\'un terrain par le retrait des eaux, par l\'extension des bois, etc.', '(a-krue)', 'n. f.', NULL, 'Maille qu\'on ajoute à chaque rangée pour accroître la largeur d\'un filet.', NULL, NULL, NULL, NULL),
('Accul', 'Lieu qui n\'a point d\'issue.', '(a-kul)', 'n. m.', 'Les voleurs, poussés dans un accul, y furent pris.', NULL, NULL, NULL, NULL, NULL),
('Acédie', 'Paresse. Du mot Latin Acedia. \" Li quars pechié de pareche, c\'on apele en clerkois accide. \" (Le Miroir, MS. cité par Du Cange, Gloss. Lat. au mot Acedia.) \",.', NULL, 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acescent', 'Terme didactique. Qui commence à devenir acide.', '(a-sè-ssan, ssant\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acétabule', 'Terme d\'antiquité. Sorte de vase destiné au vinaigre.', '(a-sé-ta-bu-l\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Achancri', 'Gangréné,.', NULL, 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ache', 'Plante ombellifère qui ressemble au persil.', '(a-ch\')', 'n. f.', 'Le front couronné d\'ache toujours verte, nous nous excitions à jouir de la vie, CHATEAUBRIAND.', NULL, NULL, NULL, NULL, NULL),
('Aciculaire', 'Terme d\'histoire naturelle. Qui est mince et allongé en forme d\'aiguille.', '(a-si-ku-lê-r\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acmé', 'Le plus haut point d\'une maladie.', '(a-kmé)', 'n. f.', 'Une maladie en son acmé. Après avoir atteint son acmé à la fin de mai, elle (la peste en Mésopotamie) déclinera en juin et disparaîtra de la Mésopotamie en juillet, THOLOZAN. Point culminant.', NULL, NULL, NULL, NULL, NULL),
('Aconché', 'Plaisant, agréable.', NULL, 'part.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aconit', 'Terme de botanique. Plante fort vénéneuse, de la famille des renonculacées.', '(a-ko-ni-t\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acope', 'Sorte de remède. Préparation médicale à bas de simples.', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acossoldahors', 'Conseillers.', NULL, 'n. m. pl.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acquéraux', 'Machines de guerre. On s\'en servoit pour jeter des pierres.', NULL, 'n. m. pl.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acquêter', 'Terme de jurisprudence. Acquérir un immeuble par un acte quelconque.', '(a-kê-ter)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acréantement', 'Promesse.', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acrologique', 'Terme de grammaire. Qui appartient au commencement d\'un mot.', '(a-kro-lo-ji-k\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Acronyque', 'Terme d\'astronomie. Se dit quand un astre se lève au coucher du soleil, ou quand il se couche au lever.', '(a-kro-ni-k\')', 'adj.', 'Coucher, lever acronyque.', NULL, NULL, NULL, NULL, NULL),
('Acrostiche', 'Ouvrage composé d\'autant de vers qu\'il y a de lettres dans le nom pris pour sujet, chaque vers commençant par une des lettres de ce nom prises de suite.', '(a-kro-sti-ch\')', 'n. m.', 'Non-seulement on fit des vers sibyllins, mais on les fit en acrostiches, VOLTAIRE.', NULL, NULL, NULL, NULL, NULL),
('Acrotère', 'Eléments d’une façade au dessus de la toiture ou d’une terrasse et qui constituent des rebords ou des garde-corps ; Piédestaux des figures que les anciens plaçaient sur les extrémités rampantes et aux sommets des frontons des temples.', '(a-kro-tê-r\')', 'n. m.', 'Les pinacles, les acrotères du temple, VOLTAIRE.', NULL, NULL, NULL, NULL, NULL),
('Acuminé', 'Se dit des feuilles, des bractées, des divisions du calice, dont l\'extrémité offre une pointe allongée et aiguë.', '(a-ku-mi-né, née)', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Adalie', 'Coccinelle marquée de deux points noirs.', NULL, 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Adamantin', 'Qui a la dureté ou l\'éclat du diamant.', '(a-da-man-tin, ti-n\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ad hominem', 'Argument attaquant directement la personne à qui l\'on s\'adresse.', '(a-do-mi-nè-m\')', 'loc. adv.', 'Argument ad hominem.', NULL, NULL, NULL, NULL, NULL),
('Ad honores', 'Pour l\'honneur, sans fonction ni émolument.', '(a-do-no-rès)', 'loc. adv.', 'C\'est une place ad honores.', NULL, NULL, NULL, NULL, NULL),
('Adextré', 'Terme de blason qui se dit des pièces qui en ont une autre à leur droite.', '(a-dè-kstré, ée)', 'adj.', 'Pal adextré d\'une croix.', NULL, NULL, NULL, NULL, NULL),
('Adieu-va', 'Terme de marine. Commandement que le timonier donne à l\'équipage d\'un bâtiment pour virer de bord vent devant.', '(a-dieu-va)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Adirer', 'Perdre, égarer. N\'est usité qu\'en jurisprudence.', '(a-di-ré)', 'v. t.', 'Adirer une pièce.', NULL, NULL, NULL, NULL, NULL),
('Adminicule', 'Terme de jurisprudence. Ce qui, sans former une preuve complète, contribue à faire preuve.', '(ad-mi-ni-ku-l\')', 'n. m.', NULL, 'Dans le langage général, secours.', 'S. plur. Ornements qui entourent la figure sur une médaille.', NULL, NULL, NULL),
('Admodiateur', 'Fermier, metayer qui prenait un heritage d\'un proprietaire pour le cultiver, et lui en rendre une partie des fruits. (Furetière).', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Admonition', 'Avertissement.', '(a-dmo-ni-sion)', 'n. f.', 'Après avoir inutilement tenté près de moi les admonitions charitables, Marcellin employa les mesures sévères, CHATEAUBRIAND.', NULL, NULL, NULL, NULL, NULL),
('Adonc', 'En ce moment, alors.', '(a-don-k)', 'adv.', 'Adonc Darius pousse sa dague, et d\'aventure n\'atteignit que le mage, P. L. COUR.', NULL, NULL, NULL, NULL, NULL),
('Adoniser', 'Parer avec une grande recherche.', '(a-do-ni-zé)', 'v. t.', NULL, 'S\'adoniser, v. pr. S\'ajuster avec un trop grand soin. Se dit surtout en parlant des hommes.', NULL, NULL, NULL, NULL),
('Adourner', 'Orner, parer, ajuster. Habiller. Apprêter, préparer. Assaisonner.', NULL, 'verbe.', 'Du latin Adornare, l\'on a fait adourner, aorner au même sens. \" Sion ahorne ta maison et si receos ton Roi. \" (St Bernard, Serm. fr. MSS. p. 381.),.', NULL, NULL, NULL, NULL, NULL),
('Adret', 'Versant d’une vallée exposé au soleil.', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Adscrit', 'Terme de grammaire. Écrit à côté.', '(ad-skri, skri-t\')', 'adj.', 'L\'\"éta\" avec un iota adscrit.', NULL, NULL, NULL, NULL, NULL),
('Adventice', 'Terme didactique. Qui survient de dehors.', '(a-dvan-ti-s\')', 'adj.', 'Idées adventices, par opposition à idées innées. Qui vient accidentellement, accessoirement.', NULL, NULL, NULL, NULL, NULL),
('Affaitage', 'Terme de fauconnerie. Éducation d\'un oiseau de proie.', '(a-fê-ta-j\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Affangissements', 'Terme d\'eaux et forêts. Amas de vase dans le lit des cours d\'eau.', '(a-fan-ji-se-man)', 'n. m. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Affanure', 'Salaire en nature que reçoivent les ouvriers employés à faire les récoltes.', '(a-fa-nu-r\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Afféage', 'Droit qui était dû pour chaque feu d\'un village.', '(a-fé-a-j\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Afféner', 'Terme d\'agriculture. Donner la pâture aux bestiaux.', '(a-fe-né)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Afféterie', 'Recherche mignarde dans les manières ou dans le langage.', '(a-fé-te-rie)', 'n. f.', 'Dont l\'oeil rit mollement avec afféterie, RÉGNIER.', NULL, NULL, NULL, NULL, NULL),
('Affidé', 'En qui on a confiance ; sur qui l\'on compte.', '(a-fi-dé, dée)', 'adj.', 'Favori de Philippe et si affidé à Alexandre que... VAUGEL.', 'Substantiv. C\'est un de ses affidés. Aposter quelqu\'un de ses affidés.', NULL, NULL, NULL, NULL),
('Affliquet', 'Petit bijou et objet de parure agrafé aux vêtements.', '(a-fli-kè)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Affouer', 'Faire du feu, l\'allumer. On a dit en ce sens : \" L\'usage per tout mes bois por affoer, por marronner , por édiffier, etc. \".', NULL, 'verbe.', NULL, NULL, NULL, NULL, NULL, NULL),
('Agape', 'Repas que les premiers chrétiens faisaient en commun.', '(a-ga-p\')', 'n. f.', 'Il faut, quand on fait le repas des agapes, envoyer les meilleurs plats à l\'évêque, VOLTAIRE.', NULL, NULL, NULL, NULL, NULL),
('Agnat', 'Terme de droit romain. Membre d\'une famille.', '(ag-na)', 'n. m.', NULL, 'N. m. plur. Terme de droit ancien. Collatéraux descendant par mâles d\'une même souche masculine. Les plus proches parents par mâles qu\'on appela agnats, MONTESQUIEU.', NULL, NULL, NULL, NULL),
('Agoniste', 'Terme d\'antiquité. Le combattant, le lutteur.', '(a-go-ni-st\')', 'n. m.', 'Il y a une vieille édition du Samson agoniste de Milton, précédée d\'un abrégé de l\'histoire de ce héros, VOLTAIRE.', NULL, NULL, NULL, NULL, NULL),
('Agora', 'Le marché, la place publique dans les villes grecques.', '(a-go-ra)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Agreste', 'Qui a un caractère de rusticité sauvage.', '(a-grè-st\')', 'adj.', 'De même que l\'espèce humaine paraît agreste, contrefaite et rapetissée dans les climats glacés du Nord... BUFFON.', 'Il se dit aussi quelquefois en parlant des personnes. Homme agreste. Manières agrestes.', NULL, NULL, NULL, NULL),
('Ahan', 'Grand effort, tel que celui que fait un homme qui fend du bois ou soulève un fardeau pesant.', '(a-an)', 'n. m.', 'Suer d\'ahan, faire une chose très pénible.', NULL, NULL, NULL, NULL, NULL),
('Aheurter', 'Se heurter à quelque chose, s\'opiniâtrer, s\'obstiner.', '(S\') (a-heur-té)', 'v. pr.', 'S\'aheurter à un sentiment, à une opinion. Elle ne s\'était jamais aheurtée à les défendre, J. J. ROUSSEAU.', NULL, NULL, NULL, NULL, NULL),
('Aiguade', 'Provision d\'eau douce pour les vaisseaux, que l\'on va prendre sur le rivage.', '(è-ga-d\')', 'n. f.', NULL, 'Endroit où l\'on peut faire de l\'eau.', NULL, NULL, NULL, NULL),
('Aiguail', 'Rosée, petites gouttes d\'eau qui demeurent sur les feuilles.', '(è-gall, ll mouillées)', 'n. m.', 'Le soleil n\'a pas bu l\'aiguail de la prairie, RACAN.', NULL, NULL, NULL, NULL, NULL),
('Aiguayer', 'Baigner, rafraîchir, laver.', '(è-gha-ié)', 'v. t.', 'Aiguayer un cheval, c\'est le faire entrer dans la rivière jusqu\'au ventre, et l\'y promener pour le laver et le rafraîchir.', NULL, NULL, NULL, NULL, NULL),
('Aigue', 'Ancien nom de l\'eau.', '(è-gh\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aiguillot', 'Terme de marine. Mamelon des gonds fixés au gouvernail d\'un bâtiment.', '(è-güi-llo, ll mouillées)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ais', 'Planche de bois.', '(ê)', 'n. m.', 'Il se trouve derrière un long ais de menuiserie que porte un ouvrier, LA BRUYÈRE.', NULL, NULL, NULL, NULL, NULL),
('Aisselle', 'En architecture, partie de la voûte d\'un four, depuis la naissance de cette voûte jusqu\'à la moitié de sa hauteur.', '(è-sè-l\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aître', 'S\'est dit pour porche d\'église.', '(ê-tr\')', 'n. m.', NULL, 'Se dit aussi d\'une espèce de galerie couverte qui entourait les cimetières.', NULL, NULL, NULL, NULL),
('Alacrité', 'État, disposition de celui qui est allègre.', '(a-la-kri-té)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Alaire', 'Terme de zoologie. Qui a rapport aux ailes.', '(a-lè-r\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Albédo', 'Rapport entre la quantité de lumière que reçoit un corps non lumineux et celle qu’il réfléchit.', '(al-bé-do)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Alcade', 'Nom de certains magistrats en Espagne.', '(al-ka-d\')', 'n. m.', 'Un cacique, un corrégidor, des régidors et des alcades formaient le corps militaire, civil et politique, des Réductions, CHATEAUBRIAND.', NULL, NULL, NULL, NULL, NULL),
('Alcyon', 'Oiseau de mer.', '(al-si-on)', 'n. m.', 'Les Poëtes ont feint que les alcyons rendoient la mer calme, pendant qu\'ils faisoient leurs petits. (Dict. de l’Académie).', NULL, NULL, NULL, NULL, NULL),
('Alérion', 'Terme de blason. Petit aigle aux ailes étendues, sans pied ni bec.', '(a-lé-ri-on)', 'n. m.', 'L\'alérion aux bonds sublimes, Qui se cabre, immense, indompté, Plein du hennissement des cimes, Dans la bleue immortalité, Victor HUGO.', NULL, NULL, NULL, NULL, NULL),
('Alezan', 'Ne s\'emploie qu\'on parlant du cheval ou de la jument. Il désigne ce genre de robe dans laquelle le corps est recouvert de poils rouges ou bruns plus ou moins foncés, les crins et les extrémités étant de même couleur ou d\'une nuance plus claire.', '(a-le-zan, za-n\', ou al-zan, za-n\')', 'Adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Alfange', 'Sorte de cimeterre.', '(al-fan-j\')', 'n. f.', 'Contre nous de pied ferme ils tirent leurs alfanges, De notre sang au leur font d\'horribles mélanges, CORNEILLE.', NULL, NULL, NULL, NULL, NULL),
('Algarade', 'Incursion militaire.', '(al-ga-ra-de)', 'n. f.', NULL, 'Vive sortie contre quelqu\'un, insulte brusque, inattendue. Oui, ventrebleu, c\'est moi, Vous venez de me faire une rude algarade, REGNARD.', NULL, NULL, NULL, NULL),
('Aliboron', 'Terme précédé de \"maître\" pour désigner l\'âne.', '(a-li-bo-ron)', 'n. m.', 'Maître aliboron. Arrive un troisième larron Qui saisit maître aliboron, LA FONTAINE.', 'Homme ignorant et stupide. C\'est un maître aliboron.', NULL, NULL, NULL, NULL),
('Alizarine', 'Principe colorant que la chimie retire de la garance, en lui faisant subir divers traitements.', '(a-li-za-ri-n\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Allége', 'Embarcation qui suit un bâtiment pour le décharger ou le charger.', '(a-lè-j\')', 'n. f.', 'Il se trouva 198 vaisseaux de guerre en comptant les alléges, VOLTAIRE.', 'En termes d\'architecture, mur d\'appui d\'une fenêtre, moins épais que l\'embrasure.', NULL, NULL, NULL, NULL),
('Alleu', 'Terme de droit féodal. Bien héréditaire.', '(a-leu)', 'n. m.', 'Tenir d\'alleu, posséder héréditairement.', 'Franc-alleu, bien héréditaire exempt de tout droit seigneurial.', NULL, NULL, NULL, NULL),
('Alliacé', 'Qui tient de l\'ail.', '(al-li-a-sé, sée)', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Allitération', 'Répétition de phonèmes consonantiques destinée à produire un effet soit harmonique soit structurel qui a le plus souvent une fonction rythmique. Pour qui Sont Ces Serpents qui Sifflent Sur vos têtes ? RACINE. Elle n’écouTe ni les gouTTes, dans leurs chuTTes, Tinter d’un siècle vide au loinTain le Trésor… VALERY.', '(al-li-té-ra-sion)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Allumelle', 'Fer delié et plat qui fait le tranchant ou la lame des épées, couteaux, poignards, etc.', NULL, 'n. f.', 'On dit proverbialement, qu\'un homme s\'est tué de sa propre allumelle, quand il a trop fait la débauche de vin ou de femmes. (Furetière 1690).', NULL, NULL, NULL, NULL, NULL),
('Almadie', 'Sorte de grande pirogue de quelques parties de l\'Afrique.', '(al-ma-die)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Almée', 'Danseuse indienne.', '(al-mée)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Alopécie', 'Chute des cheveux, des sourcils, des poils, accidentelle et prématurée ou sénile, partielle ou totale.', '(a-lo-pé-sie)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Alumelle', 'Lame de couteau ou d\'épée.', '(a-lu-mè-l\')', 'n. f.', NULL, 'Terme de marine. Petite plaque de fer qui sert à garnir la mortaise du gouvernail.', 'Outil d\'acier qui sert à polir et à achever les peignes.', NULL, NULL, NULL),
('Amarante', 'Fleur d\'automne d\'un rouge pourpre et velouté.', '(a-ma-ran-t\')', 'n. f.', NULL, 'Adj. De couleur amarante. Velours, étoffe amarante.', NULL, NULL, NULL, NULL),
('Amariner', 'Envoyer du monde à bord d\'un vaisseau pris sur l\'ennemi pour y tenir garnison.', '(a-ma-ri-né)', 'v. t.', NULL, 'Habituer à la mer.', NULL, NULL, NULL, NULL),
('Amatir', 'Rendre mat, ôter le poli.', '(a-ma-tir)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ambages', 'Circuit de paroles.', '(am-ba-j\')', 'n. f. plur.', 'Point d\'ambages, de circonlocutions, MOLIÈRE.', NULL, NULL, NULL, NULL, NULL),
('Amble', 'Allure dans laquelle le cheval lève ensemble les deux jambes du même côté, alternativement avec celles du côté opposé.', '(an-bl\')', 'n. m.', 'Le magnifique avait un cheval d\'amble, LA FONTAINE.', NULL, NULL, NULL, NULL, NULL),
('Ambleure', 'Amble.', NULL, 'subst. fém.', 'Dans un sens relatif à celui du verbe ambler, errer son ambleure a signifié aller et venir, se promener.', NULL, NULL, NULL, NULL, NULL),
('Aménité', 'Agrément accompagné de douceur.', '(a-mé-ni-té)', 'n. f.', 'Aménité d\'un lieu. Vous pourrez jouir de l\'aménité de la France, que vous aimez, MONTESQUIEU.', 'Douceur accompagnée de grâce et de politesse.', NULL, NULL, NULL, NULL),
('Amers', 'Terme de marine. Marques apparentes sur les côtes, telles que clochers, tours, rochers, propres à guider les navigateurs qui sont à vue de terre.', '(a-mêr)', 'n. m. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Amission', 'Terme de théologie et de jurisprudence. Perte.', '(a-mi-ssi-on)', 'n. f.', 'L\'amission de la grâce.', NULL, NULL, NULL, NULL, NULL),
('Amnicole', 'Terme de zoologie. Qui vit sur le bord des rivières.', '(a-mni-ko-l\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Amodier', 'Donner à ferme. Synonyme d\'affermer.', '(a-mo-di-é)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Amouille', 'Nom du premier lait fourni par une vache qui vient de vêler.', '(a-mou-ll\', ll mouillées)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Amphibologie', 'Arrangement des mots d\'où résulte un sens douteux.', '(an-fi-bo-lo-jie)', 'n. f.', 'Voici un exemple d\'amphibologie dans Molière : Et de même qu\'à vous je ne lui suis pas chère.', NULL, NULL, NULL, NULL, NULL),
('Amphigouri', 'Écrit burlesque et qu\'on remplit de galimatias.', '(an-fi-gou-ri)', 'n. m.', 'Un plaisant amphigouri.', 'Discours dépourvu d\'ordre et de sens. Pour élaguer les tortillages et les amphigouris, J. J. ROUSSEAU.', NULL, NULL, NULL, NULL),
('Amphistère', 'Terme de blason. Serpent ou dragon qui a deux ailes, et qui est souvent representé dans les Armoiries. Ce mot vient du Grec amphi, qui signifie autour, et de ptera, qui signifie aile. (Furetière).', '(an-fi-stè-r\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Amphitryon', 'Celui chez lequel, ou aux frais duquel on dîne.', '(an-fi-tri-on)', 'n. m.', 'Le véritable amphitryon Est l\'amphitryon où l\'on dîne, MOLIÈRE.', NULL, NULL, NULL, NULL, NULL),
('Amure', 'Terme de marine.', '(a-mu-r\')', 'n. f.', 'Cordage fixant le point d\'en bas, nommé point d\'amure, d\'une basse voile qui se trouve au vent.', NULL, NULL, NULL, NULL, NULL),
('Anacamptique', 'Terme de physique. Qui réfléchit, en parlant du son ou de la lumière.', '(a-na - kan - pti-k\')', 'adj.', NULL, 'En géométrie, se dit des courbes produites par la réflexion de la lumière sur une ligne ou sur une surface.', NULL, NULL, NULL, NULL),
('Anachorète', 'Religieux qui vit dans la solitude, par opposition à cénobite, religieux qui vit en communauté avec d\'autres.', '(a-na-ko-rè-t\')', 'n. m.', NULL, 'Homme qui vit loin du monde. Il mène une vie d\'anachorète.', NULL, NULL, NULL, NULL),
('Anaclastique', 'En termes de physique, se dit du point où un rayon lumineux se réfracte ou se réfléchit.', '(a-na-kla-sti-k\')', 'Adj.', 'Courbe anaclastique, courbe apparente suivant laquelle une ligne est vue à travers un milieu réfringent.', NULL, NULL, NULL, NULL, NULL),
('Anacoluthe', 'Terme de grammaire. Ellipse qui consiste à employer un relatif sans son antécédent. Je vais où va toute chose, c\'est-à-dire dans les lieux où...', '(a-na-ko-lu-t\')', 'n. f.', NULL, 'Tournure dans laquelle commençant par une construction, ou finit par une autre, comme dans ces vers de Cinna, V, 1 : Toutes les dignités que tu m\'as demandées, Je te les ai sur l\'heure et sans peine accordées.', NULL, NULL, NULL, NULL),
('Anadiplose', 'Répétition qui consiste à placer deux fois de suite le même mot à la fin de la phrase qui finit et au début de celle qui commence, pour donner plus de force à l\'expression. Et les princes et les peuples gémissaient en vain ; en vain Monsieur, en vain le roi lui-même tenait Madame serrée…BOSSUET.', '(a-na-di-plô-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Anadyomène', 'Terme d\'antiquité.', '(a-na-di-o-mè-n\')', 'adj. f.', 'Vénus anadyomène. (En grec, celle qui sort de l\'eau, du grec, en haut, et, aller.).', NULL, NULL, NULL, NULL, NULL),
('Anaglyphe', 'Terme d\'antiquité. Bas-relief ou vase orné de bas-reliefs.', '(a-na-gli-f\') ou ANAGLYPTE (ana-gli-pt\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Anagnoste', 'Terme d\'antiquité. Esclave qui, chez les riches Romains, faisait la lecture pendant le repas.', '(a-nag-no-st\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Anagogique', 'Terme de théologie.', '(a-na-go-ji-k\')', 'adj.', 'Interprétation anagogique, interprétation qui s\'élève du sens littéral à un sens spirituel. Pour distinguer le sens anagogique des phrases hébraïques chez les prophètes, VOLTAIRE.', NULL, NULL, NULL, NULL, NULL),
('Analectes', 'Terme de philologie. Morceaux, fragments choisis d\'un auteur ou de plusieurs auteurs.', '(a-na-lè-kt\')', 'n. m. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Analogon', 'Mot grec. Chose analogue, analogie.', '(a-na-lo-gon)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Anamartésie', 'Terme de dogmatique. Absence de péché.', '(a-na-mar-té-zie)', 'n. f.', 'L\'anamartésie de Jésus.', NULL, NULL, NULL, NULL, NULL),
('Anamorphose', 'Image déformée dessinée sur une surface plane, qui, réfléchie par un miroir cylindrique vertical, offre une figure régulière.', '(a-na-mor-fô-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Anaphore', 'Figure de rhétorique. Répétition du même mot en tête des phrases ou de membres de phrase.', '(a-na-fo-r\')', 'n. f.', 'Il y a une anaphore dans ces vers de Delille : Tendre épouse, c\'est toi qu\'appelait son amour, Toi qu\'il pleurait la nuit, toi qu\'il pleurait le jour.', NULL, NULL, NULL, NULL, NULL),
('Anastrophe', 'Terme de grammaire. Renversement de construction.', '(a-na-stro-f\')', 'n. f.', 'Argent comptant, tambour battant, sans bourse délier sont des anastrophes.', NULL, NULL, NULL, NULL, NULL),
('Anatocisme', 'Capitalisation des intérêts d\'une somme prêtée.', '(a-na-to-si-sm\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ancile', 'Terme d\'antiquité romaine. Bouclier sacré que les Romains croyaient tombé du ciel, et à la possession duquel ils supposaient attachée la durée de leur empire.', '(an-si-l\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Andabate', 'Terme d\'antiquité. Gladiateur qui combattait à cheval avec un bandeau sur les yeux.', '(an-da-ba-t\')', 'n. m.', 'Il me semblait que nous allions tous combattre à la façon des anciens andabates, RETZ.', NULL, NULL, NULL, NULL, NULL),
('Andain', 'Étendue que le faucheur peut faucher de pas en pas.', '(an-din)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Andrinople', 'Usité dans une locution : sorte de rouge. Il est dit aussi rouge turc.', '(an-dri-no-pl\')', 'n. f.', 'Rouge d\'Andrinople ou rouge Andrinople.', NULL, NULL, NULL, NULL, NULL),
('Angarier', 'Vexer, tourmenter. L\'homme qu\'un officier de la Bastille voit et angarie tous les jours, LINGUET.', '(an-ga-ri-é)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ange', 'En termes d\'artillerie, boulet coupé en deux, trois ou quatre parties enchaînées ensemble, dont on se servait autrefois dans les combats de mer.', '(an-j\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Angon', 'Demi-pique à l\'usage des Francs.', '(an-gon)', 'n. m.', 'D\'autres Francs tiennent une espèce de javelot nommé angon, CHATEAUBRIAND.', NULL, NULL, NULL, NULL, NULL),
('Angrois', 'Petit coin qu\'on enfonce dans l\'oeil du marteau pour en assujettir le manche.', '(an-groi)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Anguilles', 'Pièces de charpente qui soutiennent un navire au lancement.', '(an-ghi-ll\', ll mouillées)', 'n. f. pl.', NULL, NULL, NULL, NULL, NULL, NULL),
('Angustié', 'Étroit, serré, en parlant d\'un chemin.', '(an-gu-sti-é, ée)', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Animadversion', 'Improbation.', '(a-ni-mad-vèr-sion ; en poésie, de six syllabes)', 'n. f.', 'Il suscita contre lui l\'animadversion publique.', NULL, NULL, NULL, NULL, NULL),
('Annomination', 'Terme de rhétorique. Traduction ou dérivation qui s\'applique à un nom propre.', '(a-nno-mi-na-sion)', 'n. f.', 'Ainsi il y a une annomination dans ce passage de l\'Évangile de saint Matthieu, XVI, 18 : Je te dis que tu es Pierre, et sur cette pierre je bâtirai mon église.', NULL, NULL, NULL, NULL, NULL),
('Anoure', 'Terme de zoologie. Se dit des animaux qui n\'ont point de queue.', '(a-nou-r\')', 'adj. et n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ansange', 'Surface rectangulaire de 40 perches de long sur 4 perches de large, à raison d\'une perche de I0 pieds. Estimé à un peu plus ou un peu moins de I4 ares selon les auteurs contemporains.', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Anspessade', 'Dans l\'ancienne armée française, bas-officier d\'infanterie subordonné au caporal.', '(an-spè-sa-d\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antanaclase', 'Terme de rhétorique. Répétition d\'un même mot en des sens différents. Le cœur a ses raisons que la raison ignore. PASCAL.', '(an-ta-na-kla-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antanagoge', 'Figure de rhétorique. Récrimination.', '(an-ta-na-go-j\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antichrèse', 'Abandon des revenus d\'une propriété pour les intérêts d\'un emprunt.', '(an-ti-krê-z\')', 'n. f.', 'Le nantissement d\'une chose mobilière s\'appelle gage ; celui d\'une chose immobilière s\'appelle antichrèse.', NULL, NULL, NULL, NULL, NULL),
('Antilogie', 'Terme didactique. Contradiction de langage, d\'idées.', '(an-ti-lo-jie)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antiparastase', 'Figure de rhétorique, qui consiste en ce qu\'un accusé maintient qu\'il devrait être loué plutôt que blâmé, s\'il avait fait ce qu\'on lui impute.', '(an-ti-pa-ra-sta-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antiphernaux', 'Terme de pratique.', '(an-ti-fèr-nô)', 'adj. m. plur.', 'Biens antiphernaux, biens que le mari donne à sa femme par contrat de mariage.', NULL, NULL, NULL, NULL, NULL),
('Antiphonaire', 'Livre d\'église où se trouvent notés les antiennes et tous les chants de l\'office.', '(an-ti-fo-nê-r\') ou ANTIPHONIER (an-ti-fo-nié)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antiquer', 'En termes de relieur, enjoliver la tranche d\'un livre de figures de diverses couleurs ; relier à la manière antique.', '(an-ti-ké)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antitrope', 'Mot qu\'on a employé pour désigner collectivement l\'ironie, le sarcasme, l\'euphémisme.', '(an-ti-tro-p\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antizymique', 'Qui empêche la fermentation de se développer.', '(an-ti-zi-mi-k\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antonomase', 'Sorte de synecdoque qui consiste à prendre un nom commun pour un nom propre, ou un nom propre pour un nom commun. Un Zoïle pour un critique ; l\'Orateur romain pour Cicéron.', '(an-to-no-mâ-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Antonymie', 'Opposition de mots ou de noms qui offrent un sens contraire.', '(an-to-ni-mie)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apagogie', 'Terme de rhétorique. Sorte de démonstration par laquelle on fait voir la vérité d\'une chose en prouvant l\'impossibilité ou l\'absurdité du contraire.', '(a-pa-go-jie)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aparithmèse', 'Synonyme d\'énumération. Figure de pensée la plus commune qui consiste à séparer un tout en ses diverses parties, que l\'on énumère successivement.', '(a-pa-ri-tmê-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apertement', 'D\'une façon ouverte.', '(a-pèr-te-man)', 'adv.', 'Là, dans la chambre, et par tout l\'appartement, on lisait apertement sur les visages... SAINT-SIMON.', NULL, NULL, NULL, NULL, NULL),
('Apertise', 'Preuve d\'adresse, de force, de courage.', '(a-pèr-ti-z\')', 'n. f.', 'Montant chevaux à poil et faisant autres apertises que jeunes filles n\'ont point accoutumé à faire, VOLTAIRE.', NULL, NULL, NULL, NULL, NULL),
('Aphélie', 'Terme d\'astronomie. Point de l\'orbite d\'une planète où elle est le plus éloignée du soleil.', '(a-fé-lie)', 'n. f.', 'L\'aphélie est l\'opposé du périhélie.', 'Adj. La terre est aphélie, le plus éloignée du soleil.', NULL, NULL, NULL, NULL),
('Aphérèse', 'Figure de grammaire par laquelle on retranche une syllabe, un phonème ou une lettre au commencement d\'un mot. Sthène pour Démosthène.', '(a-fé-rê-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aphylle', 'Terme de botanique.', '(a-fi-l\')', 'adj.', 'Plante aphylle, plante qui n\'a pas de feuilles.', NULL, NULL, NULL, NULL, NULL),
('Apices', 'Les petits signes, tels que les accents, qu\'on mettait dans l\'écriture du grec.', '(a-pi-sès\')', 'n. m. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apocatastase', 'Terme d’antiquité. Révolution idéale ramenant, selon les anciens philosophes, les astres à un certain point pris pour point initial.', '(a-po-ka-ta-sta-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apocope', 'Figure de grammaire. Retranchement d\'une lettre, d\'une syllabe ou d’un phonème à la fin d\'un mot ; par exemple, encor pour encore, Steph pour Stéphane.', '(a-po-ko-p\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apocrisiaire', 'Terme d\'histoire. Chez les Grecs du Bas-Empire, officier primitivement chargé de porter les réponses des empereurs, et plus tard dignitaire chargé de l\'expédition des édits et des actes.', '(a-po-kri-zi-ê-r\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apodictique', 'En termes d\'école, un raisonnement démonstratif, suivant la signification grecque du mot.', '(a-po-di-kti-k\')', 'adj.', 'Un argument apodictique.', NULL, NULL, NULL, NULL, NULL),
('Apographe', 'Copie d\'un écrit original, par opposition à autographe.', '(a-po-gra-f\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apophthegme', 'Dit notable de quelque personnage illustre.', '(a-po-ftè-gm\')', 'n. m.', 'Les sentences et les apophthegmes sont les fruits recueillis du long usage et des conclusions de l\'expérience, BALZAC.', NULL, NULL, NULL, NULL, NULL),
('Aporétique', 'Embarrassant, douteux.', '(a-po- ré-ti-k\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aporie', 'Figure de rhétorique. Synonyme de dubitation.', '(a-po-rie)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aposiopèse', 'Figure de rhétorique. Synonyme de réticence.', '(a-po-si-o-pê-z\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apostasie', 'Changement de religion, et particulièrement abandon de la foi chrétienne.', '(a-po-sta-zie)', 'n. f.', NULL, 'Action d\'un religieux qui renonce à ses vœux.', 'Par extension, désertion d\'un parti, abandon d\'une doctrine, d\'une opinion.', NULL, NULL, NULL),
('Apostille', 'Annotation en marge ou au bas d\'un écrit.', '(a-po-sti-ll\', ll mouillées, et non apo-sti-ye)', 'n. f.', 'Écrire en apostille. Ce que j\'ai lu dans l\'apostille de votre lettre ne m\'a pas extrêmement plu, BALZAC.', NULL, NULL, NULL, NULL, NULL),
('Apparoir', 'Usité seulement à l\'infinitif et à la troisième personne du singulier du présent de l\'indicatif : il appert. Terme de palais. être constaté. Appert-il mieux des dispositions des hommes que par un acte signé de leur main ? LA BRUYÈRE.', '(a-pa-roir)', 'v. impers.', NULL, NULL, NULL, NULL, NULL, NULL),
('Appété', 'Désiré.', '(a-ppé-té, tée)', 'part. passé.', 'Les objets appétés par l\'âme.', NULL, NULL, NULL, NULL, NULL),
('Apside', 'Point de l\'orbite d\'une planète ou d\'un satellite où cette planète se trouve le plus près ou le plus loin du soleil, où ce satellite se trouve le plus près ou le plus loin de sa planète.', '(a-psi-d\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aptère', 'Terme d\'histoire naturelle. Qui est sans ailes. Il se dit des insectes qui n\'ont point d\'ailes.', '(a-ptê-r\')', 'adj. et n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Apyre', 'Terme de minéralogie et de chimie. Qui résiste à l\'action du feu ; infusible.', '(a-pi-r\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aqua-tinta', 'Gravure à l\'eau forte imitant le dessin au lavis.', '(a-koua-tin\'-ta) et quelquefois AQUA-TINTE (a-koua-tin-t\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aquilant', 'De couleur fauve ou brune, à peu près semblable à celle de l\'aigle, en parlant du cheval.', '(a-ki-lan) ou AQUILAIN (a-kilin)', 'adj. m. et subst.', 'Le chevalier jurait par sa durandal et son aquilain, sa fidèle épée et son coursier rapide, CHATEAUBRIAND.', NULL, NULL, NULL, NULL, NULL),
('Aquilon', 'En termes de blason, têtes d\'enfants joufflues, qui paraissent souffler avec violence.', '(a-ki-lon)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aramber', 'C\'est, accrocher un bâtiment pour venir à l\'abordage. (Furetière).', NULL, 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Arantèles', 'Filandres en forme de toile d\'araignée, qui se trouvent ordinairement au pied du cerf.', '(a-ran-tè-l\')', 'n. f. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aratoire', 'Qui sert ou qui se rapporte au labourage.', '(a-ra-toi-r\')', 'adj.', 'Instruments aratoires. Travaux aratoires.', NULL, NULL, NULL, NULL, NULL),
('Arbalestrille', 'Nom d\'un instrument aujourd\'hui abandonné qu\'on employait sur mer pour les observations de la latitude.', '(ar-balê-tri-ll\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Arbousier', 'Arbrisseau qui est du midi de l\'Europe, et qui produit des fruits doux assez semblables à la fraise pour l\'apparence.', '(ar-bou-zié)', 'n. m.', NULL, 'Arbrisseau traînant et toujours vert, qui porte des baies aigrelettes ressemblant à des cerises. Le roitelet se plaît dans ces haies de ronces et d\'arbousiers, qui sont pour lui de grandes solitudes, CHATEAUBRIAND.', NULL, NULL, NULL, NULL),
('Arbre-franc', 'C\'est un arbre enté et cultivé dans les jardins, et qui est opposé à sauvageon. (Furetière).', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Arcature', 'Terme d\'architecture. Série de petites arcades décoratives sous les appuis des fenêtres ou sous les corniches.', '(ar-ka-tu-r\')', 'n. f.', 'Tous les chapiteaux des colonnes de l\'arcature intérieure de cette église sont couverts d\'hommes, d\'animaux et de feuillages.', NULL, NULL, NULL, NULL, NULL),
('Archal', 'Laiton.', '(ar-chal)', 'n. m.', 'Usité seulement dans cette locution, fil d\'archal. Les Russes se servaient, pour leurs calculs, de petites boules enfilées dans des fils d\'archal, VOLTAIRE.', NULL, NULL, NULL, NULL, NULL),
('Archerot', 'Petit archer, nom donné à Cupidon. Vieux.', '(ar-che-ro)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Archiatre', 'Premier médecin d\'une cour, d\'un district, etc. ...', '(ar-chi-a-tr\')', 'n. m.', 'Émerveillé de la distinction subtile d\'un fameux archiatre de nos jours entre l\'assassinat positif et l\'assassinat négatif... DIDEROT.', NULL, NULL, NULL, NULL, NULL),
('Archiviole', 'Nom d\'un ancien instrument de musique. Espèce de clavecin sur lequel on appliquait un jeu de viole au moyen d\'une roue, TRÉVOUX.', '(ar-chi-vi-o-l\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Archontes', 'Titre qu\'on donnait, en Grèce et particulièrement à Athènes, aux magistrats qui dirigeaient la république.', '(ar-kon-t\')', 'n. m.', 'Il y avait chez les Athéniens neuf archontes ; leurs fonctions étaient annuelles.', NULL, NULL, NULL, NULL, NULL),
('Arcturus', 'Étoile fixe de la première grandeur, située dans la constellation du Bouvier, à la queue de la Grande Ourse.', '(ar-ktu-rus) ou poétiquement ARCTURE (ar-ktu-r\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ardélion', 'Homme qui fait l\'empressé et se mêle de tout. Inusité.', '(ar-dé-li-on)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Arder', 'Brûler. Que le feu Saint-Antoine vous arde !.', '(ar-dé)', 'V. t.', NULL, 'V. t. être brûlant. Haro ! la gorge m\'ard, LA FONTAINE.', NULL, NULL, NULL, NULL),
('Arénaire', 'Terme de botanique. Qui croît dans les terrains sablonneux.', '(a-ré-nê-r\')', 'Adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Arer', 'Terme de marine. Se dit de l\'ancre d\'un vaisseau, lorsque, le fond étant mauvais, elle n\'y tient point et laboure en quelque sorte la terre.', '(a-ré)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Argentin', 'Qui résonne comme l\'argent.', '(ar-jan-tin, ti-n\')', 'adj.', 'Son argentin. Le jet d\'eau fait toujours son murmure argentin Et le vieux tremble sa plainte sempiternelle, P. VERLAINE.', 'Qui a l\'éclat de l\'argent. Couleur argentine.', NULL, NULL, NULL, NULL),
('Argoter', 'Couper la partie morte d\'une branche.', '(ar-go-té)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Argoulet', 'Soldat à cheval armé d\'arc, les arquebuses n\'étant pas encore en usage ; on les nomma dans la suite arquebusiers à cheval, et puis dragons.', '(ar-gou-lè)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Argousier', 'Nom vulgaire de l\'hippophaé rhamnoïde, arbrisseau épineux dit aussi épine marante, épine marine et griset.', '(ar-gou-zié)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Argousin', 'Bas officier des bagnes, chargé de la garde des forçats.', '(ar-gou-zin)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Argue', 'Machine de bois dont les tireurs d\'or, d\'argent, etc., se servent pour affermir la filière.', '(ar-gh\')', 'n. f.', 'Tirer à l\'argue, c\'est passer par la filière.', NULL, NULL, NULL, NULL, NULL),
('Argutie', 'Raisonnement sur des vétilles, subtilité.', '(ar-gu-sie)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Argyraspide', 'Nom d\'un corps d\'élite de l\'armée d\'Alexandre, dont les soldats portaient un bouclier argenté.', '(ar-ji-ra-spi-d\')', 'n. m. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Argyrique', 'Qui appartient au métal argent.', '(ar-ji-ri-k\')', 'adj.', 'Sels argyriques.', NULL, NULL, NULL, NULL, NULL),
('Armadille', 'Flotte qu\'entretenait le roi d\'Espagne pour fermer aux étrangers l\'accès de ses possessions dans le nouveau monde.', '(ar-ma-di-ll\', ll mouillées)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Armeline', 'Peau très fine et fort blanche, qui appartient à l\'hermine.', '(ar-me-li-n\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Armillé', 'Terme didactique. Qui est entouré d\'un anneau.', '(ar-mil-lé, lée)', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Armet', 'Armure de tête.', '(ar-mè)', 'n. m.', 'Et ses yeux qui brillaient sous un front assuré Éclataient à l\'envi de son armet doré, TRISTAN. Fig. La tête, le cerveau.', NULL, NULL, NULL, NULL, NULL),
('Armilles', 'Petites moulures qui entourent le chapiteau dorique.', '(ar-mi-ll\', ll mouillées)', 'n. f. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Armogan', 'Terme de Marine, qui signifiait, le beau temps qui est propre pour naviger.', NULL, 'n. m.', 'Quand le Maistre perd son armogan, s\'il arrive du dommage au navire, il le doit payer au Marchand. (Furetière).', NULL, NULL, NULL, NULL, NULL),
('Armoisin', 'Taffetas léger et peu lustré.', '(ar-moi-zin)', 'n. m.', 'L\'armoisin venait d\'Italie. On en fait à Lyon, où il est aussi appelé armoise.', NULL, NULL, NULL, NULL, NULL),
('Arroi', 'Appareil, train, équipage.', '(a-roi)', 'n. m.', 'Ce personnage en magnifique arroi, RÉGNIER.', NULL, NULL, NULL, NULL, NULL),
('Arsin', 'En termes d\'eaux et forêts, bois où le feu a pris, de quelque manière qu\'il y ait été mis.', '(ar-sin)', 'adj.', 'Bois arsin.', NULL, NULL, NULL, NULL, NULL),
('Artien', 'Terme dont on se servait dans les anciennes universités pour signifier les écoliers sortis des humanités et étudiant en philosophie.', '(ar-siin)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `rares` (`word`, `def`, `pronounc`, `lexinfo`, `examples`, `def2`, `def3`, `def4`, `def5`, `def6`) VALUES
('Artimon', 'Nom de celui des mâts d\'un vaisseau qui est placé le plus près de l\'arrière ou de la poupe. La différence avec les autres, est qu\'il ne porte point de perroquets, et que la vergue le traverse de biais.', '(ar-ti-mon)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Artophage', 'Qui mange surtout du pain.', '(ar-to-fa-j\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aruspice', 'Prêtre romain qui consultait les entrailles des victimes.', '(a-ru-spi-s\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Arvicole', 'Terme d\'histoire naturelle. Qui vit dans les champs couverts de blé.', '(ar-vi-ko-l\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Arzegaye', 'Lance anciennement employée par la cavalerie ; elle était courte et ferrée par les deux bouts.', '(ar-ze-ghê)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Arzel', 'Cheval qui a les pieds de derrière blancs, avec le chanfrein blanc.', '(ar-zèl\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ascèle', 'Terme didactique. Qui n\'a point de jambes.', '(a-ssè-l\')', 'adj. et n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Asclépiade', 'Il se dit d\'un vers grec ou latin formé d\'un spondée, de deux choriambes et d\'un ïambe. Substantivement.', '(a-sklé-pi-a-d\')', 'adj. m.', 'Un asclépiade.', NULL, NULL, NULL, NULL, NULL),
('Asphodèle', 'Plante de la famille des liliacées.', '(a-sfo-dè-l\')', 'n. m.', 'Un frais parfum sortait des touffes d\'asphodèle, V. HUGO.', NULL, NULL, NULL, NULL, NULL),
('Aspiole', 'Fée, sylphe, génie.', '(a-spi-o-l\')', 'n. m.', 'Venez, boucs méchants, Psylles aux corps grêles, Aspioles frêles, Comme un flot de grêles, Fondre dans ces champs, V. HUGO.', NULL, NULL, NULL, NULL, NULL),
('Aspirail', 'Trou pratiqué dans un fourneau pour que l\'air puisse y pénétrer.', '(a-spi-rall\', ll mouillées)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Assai', 'Terme de musique. Se joint comme augmentatif au mot qui indique le mouvement d\'un air.', '(a-ssa-ie)', 'adv.', 'Presto assai, fort vite.', NULL, NULL, NULL, NULL, NULL),
('Assation', 'Terme didactique. Coction des aliments ou des médicaments dans leurs propres sucs, sans addition d\'aucune liqueur.', '(a-ssa-sion)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Assec', 'Période pendant laquelle un étang desséché est livré à la culture.', '(a-sék)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Assibilation', 'Terme de phonétique. Attribution d\'un son sifflant à une lettre qui ne l\'a pas d\'ordinaire.', '(a-ssi-bi-la-sion)', 'n. f.', 'L\'assibilation du t dans action, Revue de linguistique.', NULL, NULL, NULL, NULL, NULL),
('Assien', 'Pierre à laquelle dans l\'antiquité on attribuait la propriété de consumer les corps des morts.', '(a-ssiin, ssiè-n\')', 'adj.', 'Pierre assienne.', NULL, NULL, NULL, NULL, NULL),
('Assonance', 'Consonnance imparfaite.', '(a-so-nan-s\')', 'n. f.', NULL, 'Stylistique. Répétition remarquable d’un même phonème vocalique, soit une même voyelle, soit une combinaison de différentes voyelles qui peuvent alterner ou obéir à un effet de chiasme. Tout m’afflige et me nuit et conspire à me nuire, RACINE. Secouant dans mes yeux leurs feux diamantés, BAUDELAIRE.', NULL, NULL, NULL, NULL),
('Assoter', 'Infatuer d\'une ridicule passion.', '(a-so-té)', 'v. t.', NULL, 'S\'assoter, v. pr. Il s\'est assoté d\'une femme qui le ruinera.', NULL, NULL, NULL, NULL),
('Astragale', 'Terme d’artillerie. Espèce d\'anneau ornemental qui était sur le canon, à un demi-pied prés de la bouche. (Furetière).', '(a-stra-ga-l\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Asyndète', 'Terme de grammaire. Synonyme de disjonction, sorte d\'ellipse par laquelle on retranche les conjonctions simplement copulatives qui doivent unir les parties d\'une phrase. Français, Anglais, Lorrains, que la fureur rassemble, Avançaient, combattaient, frappaient, mouraient ensemble, VOLTAIRE.', '(a-sin-dè-t\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ataraxie', 'Terme de philosophie. Absence de trouble dans l\'âme.', '(a-ta-ra-ksie)', 'n. f.', 'L\'ataraxie même du stoïcien n\'approche pas de son indifférence, J. J. ROUSSEAU.', NULL, NULL, NULL, NULL, NULL),
('Atellanes', 'Petites pièces d\'un caractère bouffon, souvent licencieux, en usage sur le théâtre romain.', '(a-tèl-la-n\')', 'n. f. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Atêter', 'S\'attaquer à. Il s\'atêta au président de Mesme, RETZ.', '(S\') (a-tê-té)', 'v. pr.', NULL, NULL, NULL, NULL, NULL, NULL),
('Athanor', 'Nom donné, dans le moyen âge, par les alchimistes au fourneau dont ils se servaient.', '(a-ta-nor)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Atinter', 'Orner avec affectation. Populaire.', '(a-tin-té)', 'v. t.', 'S\'atinter, v. réfléchi.', NULL, NULL, NULL, NULL, NULL),
('Atlante', 'En termes d\'architecture, figure humaine chargée de quelque fardeau. Synonyme de cariatide.', '(a-tlan-t\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Atomaire', 'Terme didactique. Qui est parsemé de points colorés.', '(a-to-mê-r\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Atourner', 'Parer, mais avec un sens de familiarité ou d\'ironie.', '(a-tour-né)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Attédier', 'Autrefois, ennuyer, importuner.', '(a-té-dié)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Atticisme', 'Délicatesse de goût et de langage.', '(a-tti-si-sm\')', 'n. m.', 'Plaisanteries qui n\'avaient rien de l\'atticisme grec, VOLTAIRE.', NULL, NULL, NULL, NULL, NULL),
('Attifet', 'Ornement de tête pour les femmes.', '(a-ti-fè)', 'n. m.', 'Mais bran, bran, j\'ai laissé là-bas mon attifet, RÉGNIER.', NULL, NULL, NULL, NULL, NULL),
('Attinées', 'Lettres de défi. \" Il y eut attinés ou lettres de deffy faits de six Dauphinois contre six Bourguignons. \" Mémoires de Charles VI, p. 486.', NULL, 'n. f. pl.', NULL, NULL, NULL, NULL, NULL, NULL),
('Attrape-minon', 'Hypocrite qui attrape les simples.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
('Attrition', 'Terme de théologie. Regret d\'avoir offensé Dieu, causé par la crainte des peines.', '(a-tri-sion ; en poésie, de quatre syllabes)', 'n. f.', 'Dire que la contrition soit nécessaire, et que l\'attrition toute seule ne suffit pas avec le sacrement, PASCAL.', NULL, NULL, NULL, NULL, NULL),
('Aturré', 'Buté, entêté, endurci.', NULL, 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aubade', 'Concert donné en plein air, le plus souvent vers l\'aube du jour, à la porte ou sous les fenêtres de la personne à qui on veut faire honneur.', '(ô-ba-d\')', 'n. f.', 'Le régiment a donné une aubade à son colonel. Les gentilshommes de la garnison attaquent les donneurs d\'aubade, P. L. COUR.', NULL, NULL, NULL, NULL, NULL),
('Aubaine', 'Succession aux biens d\'un aubain, d\'un étranger non naturalisé.', '(ô-bè-n\')', 'n. f.', 'Droit d\'aubaine, droit en vertu duquel le souverain recueille la succession de l\'étranger qui meurt dans ses États. Un aigle sur un champ prétendant droit d\'aubaine, BOILEAU.', NULL, NULL, NULL, NULL, NULL),
('Aubère', 'Cheval, jument dont le corps est recouvert d\'un mélange de poils rouges et de poils blancs, la crinière et la queue étant de même couleur ou de nuance plus claire.', '(ô-bè-r\')', 'Adj.', 'Cheval, jument aubère.', 'N. m. Robe d\'un cheval aubère.', NULL, NULL, NULL, NULL),
('Aubergade', 'Droit de gîte. Ce droit était usité dans le Béarn, où l\'on disait Aubergada. Anciennement les Seigneurs, en plusieurs lieux, avaient droit de loger chez leurs sujets.', NULL, 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aubijoie', 'Le pays des Albigeois.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
('Aubin', 'Terme de manége. Allure défectueuse du cheval, qui résulte de l\'âge ou de la fatigue, et dans laquelle, galopant encore du devant, il ne peut que trotter du train de derrière.', '(ô-bin)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aucié', 'Réparé.', NULL, 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Audivit', 'Droit de se faire écouter ; autorité.', NULL, 'n. m.', '\" Il sera assis au hault bout, on luy tranchera du meilleur ; il aura l\'audivit, et le caquet par dessus tous. \" Cymbalum mundi, p. 97.', NULL, NULL, NULL, NULL, NULL),
('Auger', 'Dans les arts et métiers, creuser en gouttière une des surfaces d\'un morceau de fer plat.', '(ô-jé)', 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Augustine', 'Sorte de chaufferette où une lampe à esprit-de-vin donne la chaleur.', '(ôgu-sti-n\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aujoulet', 'Mot languedocien traduit par vieillard, dans des vers cités par Borel, au mot Marelle.', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aularques', 'Terme d\'antiquité. Prince de la cour.', '(ô-lar-k\')', 'n. m.', 'Ils (les fils de David) sont nommés, dans les Septante, aularques, c\'est-à-dire princes de la cour, pour la tenir toute unie aux intérêts de la royauté, BOSSUET.', NULL, NULL, NULL, NULL, NULL),
('Aulètride', 'Terme d\'antiquité. Joueuse de flûte.', '(ô-lè-tri-d\')', 'n. f.', 'Les peintres ont vraisemblablement voulu représenter ces saltatrices, ces aulètrides, ces mimes, H. HOUSSAYE.', NULL, NULL, NULL, NULL, NULL),
('Aulique', 'Acte que soutenait un jeune théologien ; il commençait par une harangue du chancelier de Notre-Dame, qui, à la fin de la harangue, donnait le bonnet au nouveau docteur.', '(ô-li-k\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aumailles', 'Bêtes à cornes, comme boeufs, vaches, taureaux.', '(ô-mâ-ll\', ll mouillées, et non ô-mâye)', 'adj. f. plur.', 'Bêtes aumailles.', NULL, NULL, NULL, NULL, NULL),
('Aumant', 'A l\'avenir.', '(à l\')', 'adv.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aumuce', 'Peau de martre ou de petit-gris que les chanoines et les chantres portaient sur le bras lorsqu\'ils se rendaient à l\'office. Louis XI demanda au pape la permission de porter le surplis et l\'aumusse, VOLTAIRE.', '(ô-mu-s\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aune', 'Mesure ancienne de 3 pieds 7 pouces 10 lignes 5/6, équivalant à 1m, 182.', '(ô-n\')', 'n. f.', NULL, 'La chose mesurée. Une aune de soie, de drap, de galon.', NULL, NULL, NULL, NULL),
('Auneor', 'Qui réunit. \" Aüneor de choses. \" St Bernard, Serm. Fr. MSS. p. 135, dans son latin vinitor rerum.', NULL, 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Auparager', 'Anoblir.', NULL, 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aurer', 'Guetter.', NULL, 'v. t.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aures', 'Archaïsme. Vent, souffle léger. On était caressé d\'un petit souffle que notre ancienne langue appelait l\'aure, sorte d\'avant-brise du matin, CHATEAUBRIAND.', '(ô-r\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Auriex', 'Le mois d\'avril.', NULL, 'n. m.', 'Le très douz mois, et auriex. Adam li Bocus, Poët. MSS. avant 1300, T. IV, p. 1400.', NULL, NULL, NULL, NULL, NULL),
('Aurique', 'Terme de marine. Il se dit des voiles qui ont quatre côtés, ou ralingues, sans être carrées.', '(ô-ri-k\')', 'adj.', NULL, NULL, NULL, NULL, NULL, NULL),
('Ausels', 'Oiseaux. Mot languedocien.', NULL, 'n. m. pl.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aussière', 'Terme de marine. Cordage commis avec des torons au nombre de trois ou quatre.', '(ô-siê-r\')', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
('Austrin', 'Vent du Midi.', NULL, 'adj.', 'Un vent austrin.', NULL, NULL, NULL, NULL, NULL),
('Autan', 'Vent du midi.', '(ô-tan)', 'n. m.', NULL, 'En poésie, un vent violent. Qu\'importe à l\'heureux solitaire Que l\'autan dévaste la terre S\'il ne fait qu\'agiter ses bois ? V. HUGO, Odes, IV, 2.', NULL, NULL, NULL, NULL),
('Authente', 'Terme de plain-chant. Synonyme de mode authentique.', '(ô-tan-t\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Automédon', 'Dans l\'Iliade, le conducteur du char d\'Achille.', '(ô-to-mé-don)', 'n. m.', NULL, 'Fig. et par plaisanterie, celui qui conduit une voiture.', NULL, NULL, NULL, NULL),
('Autrou', 'Maître, seigneur. Mot breton.', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Auvoire', 'Imagination, vision, croyance sans fondement.', NULL, 'n. m.', '\" Autant vaut auvoire comme bourdes proposées en justice. \" Beaumanoir.', NULL, NULL, NULL, NULL, NULL),
('Auwan', 'En cette année. « Jà ne perdrai marcées ne foire Là ù jou puisse mais awan... Gaaignerai awan assés ». Roi Guillaume.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL),
('Avalaige', 'Pente douce, chemin pour descendre.', NULL, 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Avalaison', 'Cours d\'eau torrentiel, qui se forme soudainement à la suite de pluies ou de fontes de neiges.', '(a-va-lè-zon) ou AVALASSE (a-vala-s\')', 'n. f.', NULL, 'Terme de marine. Vent d\'aval qui dure depuis plusieurs jours.', 'Amas de pierres que les eaux ont roulées et déposées sur le rivage.', NULL, NULL, NULL),
('Avanie', 'Vexations qu\'exerçaient les Turcs contre ceux qui n\'étaient pas leurs coreligionnaires, pour leur extorquer de l\'argent.', '(a-va-nie)', 'n. f.', NULL, 'Traitement humiliant, affront public. Je veux aux yeux de tous vous en faire avanie, à toute heure, en tous lieux, REGNARD.', NULL, NULL, NULL, NULL),
('Avant-duc', 'Pilotage construit à l\'entrée et sur le bord d\'une rivière.', '(a-van-duk)', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Aventurine', 'Pierre artificielle qui se fait avec du verre mêlé de limaille de cuivre, qui y éclate comme de petits grains d\'or.', '(a-van-tu-ri-n\')', 'n. f.', NULL, 'Pierre précieuse, pleine de plusieurs points d\'or qui lui donnent beaucoup de brillant ; c\'est un quartz coloré en jaune ou en rouge.', 'Adj. invariable. De couleur d\'aventurine.', NULL, NULL, NULL),
('Averne', 'Lac de la Campanie, près du quel était l\'antre de la sibylle de Cumes, antre qui conduisait, suivant la mythologie, aux enfers. Poétiquement, les enfers mêmes.', '(a-vèr-n\')', 'n. m.', NULL, NULL, NULL, NULL, NULL, NULL),
('Avertin', 'Maladie qui rend opiniâtre et furieux. Fig.', '(a-vèr-tin)', 'n. m.', 'Ô le plaisant avertin D\'un fou du pays latin, J. B. ROUSSEAU.', NULL, NULL, NULL, NULL, NULL),
