('Bacaudes', 'Paysans. En Gascogne, ces mots signifient pâtres, bergers, paysans.', NULL, 'n. m. plur.', NULL, NULL, NULL, NULL, NULL, NULL),
('Bacchanale', 'Danse bruyante et tumultueuse. Familièrement, débauche faite avec bruit.', '(ba-ka-na-l\')', 'n. f.', NULL, 'Au plur. Fêtes que les anciens célébraient en l\'honneur de Bacchus.', NULL, NULL, NULL, NULL),
('Bacbuc', 'Dans Rabelais, nom de la dive, la bienheureuse bouteille.', '(ba-kbuk)', 'n. f.', NULL, NULL, NULL, NULL, NULL, NULL),
